def print_tree(node, depth = [] of String) 
  puts "(#{node.id})"
  depth.push " " 
  len = node.children.size - 1

  (0 .. len).each do |i|
    depth.each{|c| print c} 
    unless i == len 
      print "├" 
      depth.push "│"
      print_tree node.children[i], depth
      depth.pop 
    else 
      print "└"
      depth.push " " 
      print_tree node.children[i], depth
      depth.pop
    end 
  end 
  depth.pop 
end 

