      dt = 0.001
      freefall _ = -10
      aboveGround (x, _, _, _) = x > 0
      integrate m = last $ takeWhile aboveGround $ trajectory m freefall dt p0
  print $ integrate verlet
  print $ integrate stormerVerlet
  print $ integrate velocityVerlet

